mapper.select:
SELECT ID, POST_TITLE, POST_CONTENT, MEMBER_ID, POST_READ_COUNT
FROM TBL_POST
WHERE ID = #{id}

mapper.update:
UPDATE TBL_POST
SET POST_TITLE = #{postTitle}, POST_CONTENT = #{postContent}, MEMBER_ID = #{memberId}
WHERE ID = #{id}

UPDATE TBL_POST
SET POST_READ_COUNT = POST_READ_COUNT + 1
WHERE ID = #{id}
